using AnimalCare.Client;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Web;
using System.Web.Security;
using System.Web.UI;
using System.Web.UI.WebControls;

namespace AnimalCare
{
    public partial class PageOwner : ClientPage
    {
        protected void Page_Load(object sender, EventArgs e)
        {

        }

        protected override void OnLoad(EventArgs e)
        {
            base.OnLoad(e);
            // Check if there is a profile image
            string path = Request.PhysicalApplicationPath + "Images\\" + Ctrl.Bf.OwnerID;
            if (Directory.Exists(path))
                profileImage.Attributes["src"] = "../Images/" + Ctrl.Bf.OwnerID + "/profile.jpg";
            else
                profileImage.Attributes["src"] = "data:image/svg+xml;base64,";
 
        }

    }
}